mapper.select:
	/* ReceiptMap.selectReceiptListCount */
	SELECT COUNT(*)
    FROM  RV_APPLY_TB A
    LEFT JOIN RV_PAY_TB B
    ON A.USER_ID=B.USER_ID
    AND A.APLCT_NO=B.APLCT_NO
	WHERE 1=1	

		AND A.APLCT_NO = #{aplct_no}





		AND A.INVT_NM LIKE CONCAT('%',#{invt_nm},'%')


		AND A.STATUS = #{status}

/* ReceiptMap.selectReceiptList */
SELECT A.APPLY_NO ,A.APLCT_NO ,A.APPLY_DT ,A.INVT_NM ,( case when (B.PAY_METHOD = '1') then '카드결제' when (B.PAY_METHOD = '2') then '무통장입금' when (B.PAY_METHOD = '3') then '계좌이체' else '미결제' end) as PAY_METHOD ,B.PRICE ,( case when (A.STATUS = '1') then '결제대기' when (A.STATUS = '2') then '결제완료' when (A.STATUS = '3') then '접수완료' else '납품완료' end)STATUS ,A.USER_ID ,( case when (A.FB_TYPE = '2') then '이메일' else '시스템' end) FB_TYPE FROM RV_APPLY_TB A LEFT JOIN RV_PAY_TB B ON A.USER_ID=B.USER_ID AND A.APLCT_NO=B.APLCT_NO WHERE 1=1
AND A.APLCT_NO = #{aplct_no}
AND A.INVT_NM LIKE CONCAT('%',#{invt_nm},'%')
AND A.STATUS = #{status}
ORDER BY A.APPLY_NO DESC LIMIT 10000

/*ReceiptMap.saveChoice*/
SELECT COUNT(*)
FROM RV_APPLY_TB
WHERE APLCT_NO	= #{aplct_no}
AND   USER_ID	= #{user_id}

/* ReceiptMap.duplCheck */
SELECT COUNT(*)
FROM RV_APPLY_TB
WHERE APLCT_NO = #{aplct_no}

mapper.insert:
	/* ReceiptMap.insertReceipt */
	INSERT INTO RV_APPLY_TB
	(
	     APPLY_NO
	    ,INVT_NM
	    ,APLCT_NO
	    ,APLCT_DT
	    ,APLCT_NM
	    ,MEMO
	    ,APPLY_DT
	    ,USER_ID
	    ,STATUS
	) VALUES (

	    (SELECT IFNULL ((MAX(APPLY_NO) + 1), 1) FROM RV_APPLY_TB b)
	   ,#{invt_nm}
	   ,#{aplct_no}
	   ,#{aplct_dt}
	   ,#{aplct_nm}
	   ,#{memo}
	   ,NOW()
	   ,#{user_id}
	   ,#{status}
)

mapper.update:
/*ReceiptMap.updateReceipt*/
UPDATE RV_APPLY_TB
SET INVT_NM		= #{invt_nm}
  , APLCT_NO	= #{aplct_no}
  , APLCT_DT	= #{aplct_dt}
  , APLCT_NM	= #{aplct_nm}
  , MEMO		= #{memo}
  , APPLY_DT	= NOW()
  , STATUS		= #{status}
WHERE APLCT_NO	= #{aplct_no}
AND   USER_ID	= #{user_id}

mapper.delete:
/*ReceiptMap.deleteReceipt*/
DELETE FROM RV_APPLY_TB
WHERE APLCT_NO = #{aplct_no}
AND   USER_ID  = #{user_id}

mapper.select:
/* ReceiptMap.selectReceiptFileDetail */
SELECT * FROM RV_ATTCHFILE_TB
WHERE APLCT_NO = #{aplct_no}
AND GUBUN = '1'

/* ReceiptMap.selectReceiptFileDetail_2 */
SELECT * FROM RV_ATTCHFILE_TB
WHERE APLCT_NO = #{aplct_no}
AND GUBUN = '2'

mapper.insert:
/* ReceiptMap.insertReceiptFile_2 */
INSERT INTO RV_ATTCHFILE_TB
(	
    APLCT_NO
  , FILE_NO
  , FILE_NM
  , FILE_CHNG_NM
  , FILE_PATH
  , GUBUN
) VALUES (
    #{aplct_no}

  , (SELECT IFNULL ((MAX(FILE_NO) + 1), 1) FROM RV_ATTCHFILE_TB b)
  , #{file_nm}
  , #{file_chng_nm}
  , #{file_path}
  , '2'
)

mapper.update:
	/* ReceiptMap.updateReceiptFile_2 */
	INSERT INTO RV_ATTCHFILE_TB
	(	
	    APLCT_NO
	  , FILE_NO
	  , FILE_NM
	  , FILE_PATH
	  , FILE_CHNG_NM
	  , GUBUN
	) VALUES (
	    #{aplct_no}

	  , (SELECT IFNULL ((MAX(FILE_NO) + 1), 1) FROM RV_ATTCHFILE_TB b)
	  , #{file_nm}
	  , #{file_path}
	  , #{file_chng_nm}
	  , '2'
	)
	ON DUPLICATE KEY UPDATE 
      ,APLCT_NO = #{aplct_no}
      ,FILE_NM = #{file_nm}

mapper.delete:
/* ReceiptMap.delReceiptFile_2 */
DELETE FROM RV_ATTCHFILE_TB
WHERE APLCT_NO = #{aplct_no}
AND FILE_NM = #{file_nm}
AND GUBUN = '2'
